form_model.pre_fn:
function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
  curr_obj.document_schema = {};
  curr_obj.document_schema.enabled = true;
  curr_obj.document_schema.deduplication_timing = template.element.form_model.deduplication_timing;
  curr_obj.document_schema.deduplication_policy = template.element.form_model.deduplication_policy;
  curr_obj.document_schema.deduplication_fields = template.element.form_model.deduplication_fields;
  curr_obj.document_schema.deduplication_contexts = template.element.form_model.deduplication_contexts;
  curr_obj.document_schema.custom_deduplication_configs = [];
  curr_obj.document_schema.custom_finalize_all_objects = template.element.form_model.custom_finalize_all_objects;
  curr_obj.document_schema.technology_override_schema = JSON.parse(template.element.form_model.technology_override_schema);
  return curr_obj.document_schema.custom_deduplication_configs;
}

