function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
  var obj = { 
    name: template.element.short_name,
    entry_point: "com.ikanow.aleph2.analytics.services.DeduplicationService",
    config: {}
  };
  a2_inject_enrichment(curr_obj, obj);
  return obj;
}

form_model.post_fn:
function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {

  var obj_to_mod;
  if (Array.isArray(curr_obj)) {
    obj_to_mod = curr_obj[curr_obj.length - 1];
  }
  else if (curr_obj.config) { 
    if (curr_obj.config.enrich_pipeline) {
	    obj_to_mod = curr_obj.config.enrich_pipeline[curr_obj.config.enrich_pipeline.length - 1];      
  	}
  	else obj_to_mod = curr_obj.config; 
  } 
  else obj_to_mod = curr_obj;

  obj_to_mod.config.doc_schema_override = obj_to_mod.document_schema;
  obj_to_mod.document_schema = undefined;
}

