}

function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) { 
  var new_obj = { inputs: [] };
  new_obj.analytic_type = template.element.form_model.analytic_type; 
  if (template.element.form_model.dependencies && (template.element.form_model.dependencies.length > 0)) {
  	new_obj.dependencies = (template.element.form_model.dependencies || '').replace("\\s*,\\s*", ",").split(",");
  }
  new_obj.analytic_technology_name_or_id = template.element.form_model.analytic_technology_name_or_id; 
  new_obj.module_name_or_id = template.element.form_model.module_name_or_id; 
  new_obj.entry_point = template.element.form_model.entry_point; 
  new_obj.library_names_or_ids = template.element.form_model.library_names_or_ids; 
  if (template.element.form_model.node_rules && (template.element.form_model.node_rules.length > 0)) {
	  new_obj.node_rules = (template.element.form_model.node_rules || '').replace("\\s*,\\s*", ",").split(",");
  }
  new_obj.config = JSON.parse(template.element.form_model.config || '{}'); 
  curr_obj.jobs.push(new_obj);
  return new_obj;
}

