post_building_function.$fn:
function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
  var lib_map = {}; var curr_job = curr_obj.jobs[curr_obj.jobs.length-1]; 
  for ( var e in curr_job.config.enrich_pipeline ) { 
    if (curr_job.config.enrich_pipeline[e].module_name_or_id) {
      lib_map[curr_job.config.enrich_pipeline[e].module_name_or_id] = true;
    }
    for ( l in curr_job.config.enrich_pipeline[e].library_names_or_ids ) { 
      lib_map[curr_job.config.enrich_pipeline[e].library_names_or_ids[l]] = true;
    }
  } 
  curr_job.library_names_or_ids = [];
  var libs = (template.element.form_model.library_names_or_ids || []); 
  for (var kk in libs) curr_job.library_names_or_ids.push(libs[kk]); 
  for (var k in lib_map) curr_job.library_names_or_ids.push(k); 
}

