function(errs, template, curr_obj, all_templates, root_obj, hierarchy, rows, cols) {
   curr_obj.graph_schema.custom_decomposition_configs = curr_obj.graph_schema.custom_decomposition_configs || [];
   curr_obj.graph_schema.custom_merge_configs = curr_obj.graph_schema.custom_merge_configs || [];
  for (var x in curr_obj.graph_schema.custom_configs) {
    var el = curr_obj.graph_schema.custom_configs[x];
    if (null != el.name) {
      if (el.name.startsWith("merge")) {
        curr_obj.graph_schema.custom_merge_configs.push(el);
      }
      if (el.name.startsWith("decomp")) {
        curr_obj.graph_schema.custom_decomposition_configs.push(el);
      }
    }
  }
  delete curr_obj.graph_schema.custom_configs;
}

